import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [wordData, setWordData] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);

  useEffect(() => {
    // wordlist.json „Éï„Ç°„Ç§„É´„Çí„Éï„Çß„ÉÉ„ÉÅ
    fetch('wordlist.json')
      .then((response) => response.json())
      .then((data) => setWordData(data));
  }, []);

  useEffect(() => {
    if (wordData.length > 0) {
      selectRandomQuestion();
    }
  }, [wordData]);

  const speak = (text) => {
    const speech = new SpeechSynthesisUtterance(text);
    window.speechSynthesis.speak(speech);
  };

  const selectRandomQuestion = () => {
    const randomIndex = Math.floor(Math.random() * wordData.length);
    const question = wordData[randomIndex];
    const choices = [];
    choices.push(question.chinese);
    // ‰ªñ„ÅÆÈÅ∏ÊäûËÇ¢„ÇíËøΩÂä†
    while (choices.length < 3) {
      const randomChoice = wordData[Math.floor(Math.random() * wordData.length)].chinese;
      if (!choices.includes(randomChoice)) {
        choices.push(randomChoice);
      }
    }
    // ÈÅ∏ÊäûËÇ¢„Çí„Ç∑„É£„ÉÉ„Éï„É´
    choices.sort(() => Math.random() - 0.5);
    setCurrentQuestion({ ...question, choices });
  };

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setIsCorrect(answer === currentQuestion.chinese);
  };

  const handleNextClick = () => {
    setSelectedAnswer(null);
    setIsCorrect(null);
    selectRandomQuestion();
  };

  if (!currentQuestion) {
    return <div>Loading...</div>;
  }

  return (
    <div className="app">
      <div className="container">
        <h1 className="question">
          {currentQuestion.japanese} „ÅØ„Å©„Çì„Å™ÊÑèÂë≥„Åß„Åó„Çá„ÅÜÔºü
          <button onClick={() => speak(currentQuestion.japanese)} className="speak-button">
            üó£
          </button>
        </h1>
        <div className="choices">
          {currentQuestion.choices.map((choice, index) => (
            <div key={index}>
              <button
                onClick={() => handleAnswerClick(choice)}
                disabled={selectedAnswer !== null}
                className={`button ${selectedAnswer === choice ? (isCorrect ? 'correct' : 'wrong') : ''}`}
              >
                {choice}
              </button>
              {selectedAnswer === choice && (
                <span className={`result ${isCorrect ? 'correct' : 'wrong'}`}>{isCorrect ? 'Ê≠£Ëß£' : '‰∏çÊ≠£Ëß£'}</span>
              )}
            </div>
          ))}
        </div>
        {selectedAnswer !== null && (
          <button onClick={handleNextClick} className="button next-button">
            Ê¨°„ÅÆÂïèÈ°å
          </button>
        )}
      </div>
    </div>
  );
}

export default App;
