import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [quizData, setQuizData] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [score, setScore] = useState(0);

  useEffect(() => {
    fetch('quizData.json')
      .then((response) => response.json())
      .then((data) => setQuizData(data));
  }, []);

  const handleAnswerClick = (answerIndex) => {
    setSelectedAnswer(answerIndex);
    const correct = answerIndex === quizData[currentQuestion].correct;
    setIsCorrect(correct);
    if (correct) {
      setScore(score + 1);
    }
  };

  const handleNextClick = () => {
    if (currentQuestion < quizData.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedAnswer(null);
      setIsCorrect(null);
    }
  };

  if (quizData.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div className="app">
      <div className="score">„Çπ„Ç≥„Ç¢: {score}</div>
      <div className="container">
        <h1 className="question">{quizData[currentQuestion].question}</h1>
        {quizData[currentQuestion].choices.map((choice, index) => (
          <div key={index} className="choice">
            <button onClick={() => handleAnswerClick(index)} disabled={selectedAnswer !== null} className="button">
              {choice}
            </button>
            {selectedAnswer === index && (
              <span className="result">
                {isCorrect ? 'üéâÊ≠£Ëß£üéâ' : '‰∏çÊ≠£Ëß£'}
              </span>
            )}
          </div>
        ))}
        {isCorrect && (
          <img
            src={`${process.env.PUBLIC_URL}/${quizData[currentQuestion].image}`}
            alt="related"
            className="image"
          />
        )}
        <button onClick={handleNextClick} disabled={selectedAnswer === null} className="button next-button">Ê¨°„ÅÆÂïèÈ°å</button>
      </div>
    </div>
  );
}

export default App;
