import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [wordData, setWordData] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState({});
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);

  useEffect(() => {
    // wordlist.json „Éï„Ç°„Ç§„É´„Çí„Éï„Çß„ÉÉ„ÉÅ
    fetch('wordlist.json')
      .then((response) => response.json())
      .then((data) => setWordData(data));
  }, []);

  const speak = (text, lang) => {
    const speech = new SpeechSynthesisUtterance(text);
    speech.lang = lang;
    window.speechSynthesis.speak(speech);
  };

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setIsCorrect(answer === currentQuestion.chinese);
  };

  const handleNextClick = () => {
    const newQuestion = getRandomQuestion();
    setCurrentQuestion(newQuestion);
    setSelectedAnswer(null);
    setIsCorrect(null);
  };

  const getRandomQuestion = () => {
    const randomIndex = Math.floor(Math.random() * wordData.length);
    return wordData[randomIndex];
  };

  useEffect(() => {
    handleNextClick();
  }, [wordData]);

  return (
    <div className="app">
      <div className="container">
        <h1 className="question">
          {currentQuestion.japanese} „ÅØ„Å©„Çì„Å™ÊÑèÂë≥„Åß„Åó„Çá„ÅÜÔºü
          <button onClick={() => speak(currentQuestion.japanese, 'ja-JP')} className="speak-button">
            üó£
          </button>
        </h1>
        <div className="choices">
          {currentQuestion.choices && currentQuestion.choices.map((choice, index) => (
            <div key={index}>
              <button
                onClick={() => handleAnswerClick(choice)}
                disabled={selectedAnswer !== null}
                className={`choice-button ${selectedAnswer === choice ? (isCorrect ? 'correct' : 'incorrect') : ''}`}
              >
                {choice}
                <button onClick={() => speak(choice, 'zh-CN')} className="speak-button">
                  üó£
                </button>
              </button>
            </div>
          ))}
        </div>
        {selectedAnswer !== null && (
          <div className={`result ${isCorrect ? 'correct' : 'incorrect'}`}>
            {isCorrect ? 'Ê≠£Ëß£' : '‰∏çÊ≠£Ëß£'}: {currentQuestion.chinese}
          </div>
        )}
        <button onClick={handleNextClick} className="next-button">
          Ê¨°„ÅÆÂïèÈ°å
        </button>
      </div>
    </div>
  );
}

export default App;
