import React, { useState, useEffect } from 'react';

function App() {
  const [wordData, setWordData] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);

  useEffect(() => {
    // wordlist.json ファイルをフェッチ
    fetch('wordlist.json')
      .then((response) => response.json())
      .then((data) => setWordData(data));
  }, []);

  useEffect(() => {
    if (wordData.length > 0) {
      selectRandomQuestion();
    }
  }, [wordData]);

  const selectRandomQuestion = () => {
    const randomIndex = Math.floor(Math.random() * wordData.length);
    setCurrentQuestion(wordData[randomIndex]);
  };

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setIsCorrect(answer === currentQuestion.chinese);
  };

  const handleNextClick = () => {
    selectRandomQuestion();
    setSelectedAnswer(null);
    setIsCorrect(null);
  };

  if (wordData.length === 0 || !currentQuestion) {
    return <div>Loading...</div>;
  }

  return (
    <div className="app">
      <div className="container">
        <h1 className="question">{currentQuestion.japanese} はどんな意味でしょう？</h1>
        <div className="choices">
          {currentQuestion.choices.map((choice, index) => (
            <div key={index}>
              <button
                onClick={() => handleAnswerClick(choice)}
                disabled={selectedAnswer !== null}
                className="button"
              >
                {choice}
              </button>
              {selectedAnswer === choice && (
                <span className="result" style={{ color: isCorrect ? 'blue' : 'red' }}>
                  {isCorrect ? '正解' : '不正解'}
                </span>
              )}
            </div>
          ))}
        </div>
        <button onClick={handleNextClick} disabled={selectedAnswer === null} className="button next-button">
          次の問題
        </button>
        {isCorrect !== null && (
          <div style={{ color: isCorrect ? 'blue' : 'red' }}>
            <p>Chinese: {currentQuestion.chinese}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
