import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  // ... 他のコードは変更なし

  return (
    <div className="app">
      <div className="container">
        <h1 className="question">{quizData[currentQuestion].question}</h1>
        {quizData[currentQuestion].choices.map((choice, index) => (
          <div key={index} className="choice">
            <button onClick={() => handleAnswerClick(choice)} disabled={selectedAnswer !== null} className="button">
              {choice}
            </button>
            {selectedAnswer === choice && (
              <span className="result">{isCorrect && choice === quizData[currentQuestion].correct ? '正解' : '不正解'}</span>
            )}
          </div>
        ))}
        {isCorrect !== null && (
          <img
            src={`${process.env.PUBLIC_URL}/${quizData[currentQuestion].image}`}
            alt="related"
            className="image"
          />
        )}
        <button onClick={handleNextClick} disabled={selectedAnswer === null} className="button next-button">次の問題</button>
      </div>
    </div>
  );
}

export default App;
