import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [quizData, setQuizData] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);

  useEffect(() => {
    fetch('quizData.json')
      .then((response) => response.json())
      .then((data) => setQuizData(data));
  }, []);

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setIsCorrect(answer === quizData[currentQuestion].correct);
  };

  const handleNextClick = () => {
    if (currentQuestion < quizData.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedAnswer(null);
      setIsCorrect(null);
    }
  };

  if (quizData.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div className="app">
      <h1 className="question">{quizData[currentQuestion].question}</h1>
      {quizData[currentQuestion].choices.map((choice, index) => (
        <div key={index} className="choice">
          <button onClick={() => handleAnswerClick(choice)} disabled={selectedAnswer !== null} className="button">
            {choice}
          </button>
          {selectedAnswer === choice && (
            <span>{isCorrect && choice === quizData[currentQuestion].correct ? '正解' : '不正解'}</span>
          )}
        </div>
      ))}
      {isCorrect !== null && (
        <img
          src={`${process.env.PUBLIC_URL}/${quizData[currentQuestion].image}`}
          alt="related"
          width="200"
          className="image"
        />
      )}
      <button onClick={handleNextClick} disabled={selectedAnswer === null} className="button next-button">次の問題</button>
    </div>
  );
}

export default App;
