import React, { useState, useEffect } from 'react';
import Fireworks from 'react-fireworks';
import './App.css';

function App() {
  const [quizData, setQuizData] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showFireworks, setShowFireworks] = useState(false);

  useEffect(() => {
    fetch('quizData.json')
      .then((response) => response.json())
      .then((data) => setQuizData(data));
  }, []);

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    const correct = answer === quizData[currentQuestion].correct;
    setIsCorrect(correct);
    if (correct) {
      setShowFireworks(true);
      setTimeout(() => setShowFireworks(false), 2000); // 2秒後に花火を非表示にする
    }
  };

  const handleNextClick = () => {
    if (currentQuestion < quizData.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedAnswer(null);
      setIsCorrect(null);
    }
  };

  if (quizData.length === 0) {
    return <div>Loading...</div>;
  }

  const fireworksOptions = {
    colors: ['#ffd700', '#ff5733', '#33ff57', '#3357ff'], // 花火の色を設定
    calcParticles: 100, // 花火の粒子数を設定
  };

  return (
    <div className="app">
      {showFireworks && <Fireworks options={fireworksOptions} />}
      <div className="container">
        <h1 className="question">{quizData[currentQuestion].question}</h1>
        {quizData[currentQuestion].choices.map((choice, index) => (
          <div key={index} className="choice">
            <button onClick={() => handleAnswerClick(choice)} disabled={selectedAnswer !== null} className="button">
              {choice}
            </button>
            {selectedAnswer === choice && (
              <span className="result">{isCorrect && choice === quizData[currentQuestion].correct ? '正解' : '不正解'}</span>
            )}
          </div>
        ))}
        {isCorrect !== null && (
          <img
            src={`${process.env.PUBLIC_URL}/${quizData[currentQuestion].image}`}
            alt="related"
            className="image"
          />
        )}
        <button onClick={handleNextClick} disabled={selectedAnswer === null} className="button next-button">次の問題</button>
      </div>
    </div>
  );
}

export default App;
